import 'dart:convert';import 'package:http/http.dart';class Network{  final String url;  final Map data;  Network(this.url, this.data);  Future<dynamic> getData() async {    print('Calling url: $url');    Response response = await post(url, headers: <String, String>{      'Content-Type': 'application/json',    },      body: jsonEncode(data)    );    if(response.statusCode == 200) {      return response.body;    } else {      print(response.statusCode);    }  }}