import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';import 'package:flutter_interview/presentation/home.dart';class Login extends StatefulWidget {  @override  _LoginState createState() => _LoginState();}class _LoginState extends State<Login> {  final _phoneController = TextEditingController();  final _codeController = TextEditingController();  Future<bool> loginUser(String phone, BuildContext context) async {    FirebaseAuth _auth = FirebaseAuth.instance;    _auth.verifyPhoneNumber(        phoneNumber: phone,        timeout: Duration(seconds: 60),        verificationCompleted: (AuthCredential credential) async {          Navigator.of(context).pop();          AuthResult result = await _auth.signInWithCredential(credential);          FirebaseUser user = result.user;          if (user != null) {            Navigator.push(                context, MaterialPageRoute(builder: (context) => Home()));          } else {            print("Error");          }          //This callback would gets called when verification is done auto maticlly        },        verificationFailed: (AuthException exception) {          print(exception);        },        codeSent: (String verificationId, [int forceResendingToken]) {          showDialog(              context: context,              barrierDismissible: false,              builder: (context) {                return AlertDialog(                  title: Text("Give the code?"),                  content: Column(                    mainAxisSize: MainAxisSize.min,                    children: <Widget>[                      TextField(                        controller: _codeController,                      ),                    ],                  ),                  actions: <Widget>[                    FlatButton(                      child: Text("Confirm"),                      textColor: Colors.white,                      color: Colors.blue,                      onPressed: () async {                        final code = _codeController.text.trim();                        AuthCredential credential =                            PhoneAuthProvider.getCredential(                                verificationId: verificationId, smsCode: code);                        AuthResult result =                            await _auth.signInWithCredential(credential);                        FirebaseUser user = result.user;                        if (user != null) {                          Navigator.push(context,                              MaterialPageRoute(builder: (context) => Home()));                        } else {                          print("Error");                        }                      },                    )                  ],                );              });        },        codeAutoRetrievalTimeout: null);  }  List<String> _gender = ['male', 'female'];  String selectedGender;  List<String> _age = ['below 18', 'above 18']; // Option 2  String selectedAge;  @override  Widget build(BuildContext context) {    return Scaffold(        body: SingleChildScrollView(      child: Container(        margin: EdgeInsets.symmetric(horizontal: 30, vertical: 100),        child: Form(          child: Column(            crossAxisAlignment: CrossAxisAlignment.start,            mainAxisAlignment: MainAxisAlignment.center,            children: <Widget>[              Text(                "Login",                style: TextStyle(                    color: Colors.green,                    fontSize: 36,                    fontWeight: FontWeight.w500),              ),              SizedBox(                height: 16,              ),              TextFormField(keyboardType: TextInputType.phone,                decoration: InputDecoration(                    enabledBorder: OutlineInputBorder(                        borderRadius: BorderRadius.all(Radius.circular(8)),                        borderSide: BorderSide(color: Colors.grey[200])),                    focusedBorder: OutlineInputBorder(                        borderRadius: BorderRadius.all(Radius.circular(8)),                        borderSide: BorderSide(color: Colors.grey[300])),                    filled: true,                    fillColor: Colors.grey[100],                    hintText: "Mobile Number"),                controller: _phoneController,              ),              SizedBox(                height: 16,              ),              DropdownButton(                hint: Text('choose your gender'),                value: selectedGender,                onChanged: (genVal) {                  setState(() {                    selectedGender = genVal;                  });                },                items: _gender.map((location) {                  return DropdownMenuItem(                    child: new Text(location),                    value: location,                  );                }).toList(),              ),              SizedBox(                height: 16,              ),              DropdownButton(                hint: Text('choose your age'),                value: selectedAge,                onChanged: (ageVal) {                  setState(() {                    selectedAge = ageVal;                  });                },                items: _age.map((location) {                  return DropdownMenuItem(                    child: new Text(location),                    value: location,                  );                }).toList(),              ),              SizedBox(                height: 16,              ),              Container(                width: double.infinity,                child: FlatButton(                  child: Text("LOGIN"),                  textColor: Colors.white,                  padding: EdgeInsets.all(16),                  onPressed: () {                    final phone = _phoneController.text.trim();                    loginUser(phone, context);                  },                  color: Colors.blue,                ),              )            ],          ),        ),      ),    ));  }}