import 'package:flutter/material.dart';class BottomBar extends StatefulWidget {  dynamic onUpdate;  int isSelectedIndex;  BottomBar(this.isSelectedIndex, this.onUpdate);  @override  _BottomBarState createState() => _BottomBarState();}class _BottomBarState extends State<BottomBar> {  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        borderRadius: BorderRadius.only(topLeft: Radius.circular(30),topRight: Radius.circular(30)),        color: Colors.white,      ),      height: MediaQuery.of(context).size.height * .07,      width: MediaQuery.of(context).size.width,      child: Row(        mainAxisSize: MainAxisSize.min,        crossAxisAlignment: CrossAxisAlignment.end,        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: [          buildBarIcon(              context,              Icons.home,              widget.isSelectedIndex == 0 ? Colors.green[100] : null,              widget.isSelectedIndex == 0 ? Colors.green[600] : Colors.grey,              0),          buildBarIcon(              context,              Icons.shopping_cart,              widget.isSelectedIndex == 1 ? Colors.green[100] : null,              widget.isSelectedIndex == 1 ? Colors.green[600] : Colors.grey,              1),        ],      ),    );  }  buildBarIcon(BuildContext context, icon, conColor, color, int index) {    return GestureDetector(      onTap: () {        widget.onUpdate(index);      },      child: Container(        key: Key(index.toString()),        width: MediaQuery.of(context).size.width * .15,        height: MediaQuery.of(context).size.height * .06,        decoration: BoxDecoration(          borderRadius: BorderRadius.circular(10),          color: conColor,        ),        child: Icon(          icon,          size: 25,          color: color,        ),      ),    );  }}