import 'package:flutter/material.dart';import 'package:flutter_interview/presentation/custom/item.dart';import 'package:flutter_interview/presentation/kid.dart';import 'package:flutter_interview/presentation/men.dart';class ItemHome extends StatefulWidget {  @override  _ItemHomeState createState() => _ItemHomeState();}class _ItemHomeState extends State<ItemHome>    with SingleTickerProviderStateMixin {  TabController _tabController;  @override  void initState() {    _tabController = TabController(length: 2, vsync: this);    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.indigo[50],      appBar: AppBar(        elevation: 0,        backgroundColor: Colors.indigo[50],        leading: Icon(Icons.view_list),        actions: [          Container(            height: 10,            width: 39,            decoration:                BoxDecoration(shape: BoxShape.circle, color: Colors.white),            child: IconButton(              onPressed: () {},              icon: Icon(                Icons.shopping_cart,                color: Colors.teal[100],              ),            ),          ),          SizedBox(            width: 10,          ),          Padding(            padding: const EdgeInsets.all(8.0),            child: CircleAvatar(              radius: 20,              backgroundImage: NetworkImage(                  'https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_640.png'),            ),          )        ],      ),      body: Container(        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        child: Column(          mainAxisSize: MainAxisSize.min,          mainAxisAlignment: MainAxisAlignment.start,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            SizedBox(              height: 20,            ),            buildSearchBar(context),            SizedBox(              height: 20,            ),            Container(                // color: Colors.yellow,                height: MediaQuery.of(context).size.height * .1,                child: Item()),            SizedBox(              height: 10,            ),            Container(              //  color: Colors.indigo,              height: MediaQuery.of(context).size.height * .05,              child: TabBar(                controller: _tabController,                tabs: [                  Padding(                    padding: const EdgeInsets.all(10.0),                    child: Center(                        child: Text(                      'kid’s-fashion',                      style: TextStyle(                          fontWeight: FontWeight.bold, fontSize: 15,color: Colors.black),                    )),                  ),                  Padding(                    padding: const EdgeInsets.all(10.0),                    child: Center(                        child: Text(                      'Men’s-fashion',                      style: TextStyle(                          fontWeight: FontWeight.bold,fontSize: 15, color: Colors.black),                    )),                  ),                ],              ),            ),            Container(              height: MediaQuery.of(context).size.height*.52, // tab height              child: TabBarView(                controller: _tabController,                children: [Kids(), Men()],              ),            ),          ],        ),      ),    );  }  Widget buildSearchBar(BuildContext context) => Container(        height: MediaQuery.of(context).size.height * .07,        width: MediaQuery.of(context).size.width,        decoration: BoxDecoration(borderRadius: BorderRadius.circular(20)),        child: Card(          shape:              RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),          margin: EdgeInsets.symmetric(horizontal: 20),          child: Row(            mainAxisAlignment: MainAxisAlignment.spaceAround,            children: [              Text(                'Search',                style: TextStyle(                    color: Colors.teal[100], fontWeight: FontWeight.bold),              ),              SizedBox(                width: 60,              ),              Icon(Icons.search),            ],          ),        ),      );}